function Quiz(quiz) {
    this.quizId = quiz.quiz_id
    this.name = quiz.name
    this.shortName = quiz.short_name
    this.description = quiz.description
    this.datetime = new Date(quiz.datetime)
    this.cost = quiz.cost
    this.placeId = quiz.place_id
    this.organizerId = quiz.organizer_id
    this.category = new Category(quiz.category)

    this.albumId = quiz.album_id
    this.participants = quiz.participants
    this.result = quiz.result
}

Quiz.prototype.BuildInfo = function(places, organizers, users) {
    let info = MakeElement("info", null, {id: `quiz-${this.quizId}-info`})
    let inputs = MakeElement("quiz-inputs", info)

    let dateInput = MakeIconInput(inputs, "дата", QUIZ_DATE_ICON, `quiz-${this.quizId}-date`, "basic-input", {placeholder: "дата", type: "date", value: FormatInputDate(this.datetime)})
    this.dateInput = new TextInput(dateInput, {empty: "Дата квиза не заполнена", change: () => this.Update()})

    let timeInput = MakeIconInput(inputs, "время", QUIZ_TIME_ICON, `quiz-${this.quizId}-time`, "basic-input", {placeholder: "дата", type: "text", value: FormatInputTime(this.datetime)})
    this.timeInput = new TextInput(timeInput, {empty: "Время квиза не заполнено", regexp: {expression: /^(0?\d|1\d|2[0123]):[0-5]\d?$/gi, error: "Время квиза введено некорректно"}, change: () => this.Update()})

    let nameInput = MakeIconInput(inputs, "название", QUIZ_NAME_ICON, `quiz-${this.quizId}-name`, "basic-input", {placeholder: "название", type: "text", value: this.name})
    this.nameInput = new TextInput(nameInput, {empty: "Название квиза не заполнено", change: () => this.Update()})

    let shortNameInput = MakeIconInput(inputs, "сокращение", QUIZ_SHORT_NAME_ICON, `quiz-${this.quizId}-short-name`, "basic-input", {placeholder: "сокращение", type: "text", value: this.shortName})
    this.shortNameInput = new TextInput(shortNameInput, {empty: "Сокращение квиза не заполнено", change: () => this.Update()})

    let descriptionInput = MakeIconInput(inputs, "описание", QUIZ_DESCRIPTION_ICON, `quiz-${this.quizId}-description`, "basic-textarea", {placeholder: "описание", innerText: this.description})
    this.descriptionInput = new TextInput(descriptionInput, {empty: "Описание квиза не заполнено", change: () => this.Update()})

    let costInput = MakeIconInput(inputs, "стоимость", QUIZ_COST_ICON, `quiz-${this.quizId}-cost`, "basic-input", {placeholder: "стоимость", type: "text", "input-mode": "numeric", value: this.cost})
    this.costInput = new TextInput(costInput, {empty: "Стоимость квиза не заполнена", regexp: {expression: /^\d+$/gi, error: "Стоимость квиза введена некорректно"}, change: () => this.Update()})

    this.placeInput = MakeIconInput(inputs, "место проведения", QUIZ_PLACE_ICON, `quiz-${this.quizId}-place`, "basic-select", {})
    this.placeInput.addEventListener("change", () => this.Update())
    for (let place of places)
        MakeElement("", this.placeInput, {value: place.placeId, innerText: place.name}, "option")
    this.placeInput.value = this.placeId

    this.organizerInput = MakeIconInput(inputs, "организатор", QUIZ_ORGANIZER_ICON, `quiz-${this.quizId}-organizer`, "basic-select", {})
    this.organizerInput.addEventListener("change", () => this.Update())
    for (let organizer of organizers)
        MakeElement("", this.organizerInput, {value: organizer.organizerId, innerText: organizer.name}, "option")
    this.organizerInput.value = this.organizerId

    this.categoryInput = MakeIconInput(inputs, "категория", QUIZ_CATEGORY_ICON, `quiz-${this.quizId}-category`, "basic-select", {})
    this.categoryInput.addEventListener("change", () => this.Update())
    this.category.FillSelectOptions(this.categoryInput)

    let positionInput = MakeIconInput(inputs, "место", QUIZ_POSITION_ICON, `quiz-${this.quizId}-position`, "basic-input", {placeholder: "место", type: "text", "input-mode": "numeric", value: this.result ? this.result.position : ""})
    this.positionInput = new TextInput(positionInput, {regexp: {expression: /^\d*$/gi, error: "Место введено некорректно"}, change: () => this.Update()})

    let teamsInput = MakeIconInput(inputs, "количество команд", QUIZ_TEAMS_ICON, `quiz-${this.quizId}-teams`, "basic-input", {placeholder: "количество команд", type: "text", "input-mode": "numeric", value: this.result ? this.result.teams : ""})
    this.teamsInput = new TextInput(teamsInput, {regexp: {expression: /^\d*$/gi, error: "Количество команд введено некорректно"}, change: () => this.Update()})

    let playersInput = MakeIconInput(inputs, "количество участников", QUIZ_PLAYERS_ICON, `quiz-${this.quizId}-players`, "basic-input", {placeholder: "количество участников", type: "text", "input-mode": "numeric", value: this.result ? this.result.players : ""})
    this.playersInput = new TextInput(playersInput, {regexp: {expression: /^\d*$/gi, error: "Количество участников введено некорректно"}, change: () => this.Update()})

    let participantsInput = MakeIconInput(inputs, "участники", QUIZ_PARTICIPANTS_ICON, `quiz-${this.quizId}-participants`, "", {})
    this.participantsInput = new UserSelect(users, {multiple: false})
    this.participantsInput.Build(participantsInput)

    for (let username of this.participants)
        this.participantsInput.SelectUser(username)

    this.participantsInput.onchange = () => this.Update()

    return info
}

Quiz.prototype.FormatDatetime = function() {
    return FormatDatetime(this.datetime)
}

Quiz.prototype.FormatTime = function() {
    return FormatTime(this.datetime)
}

Quiz.prototype.FormatCost = function() {
    return `${this.cost} руб.`
}

Quiz.prototype.HaveDescription = function() {
    return this.description.length > 0
}

Quiz.prototype.HavePosition = function() {
    return this.result !== null && this.result.teams > 0
}

Quiz.prototype.HavePlayers = function() {
    return this.result !== null && this.result.players > 0
}

Quiz.prototype.FormatPosition = function() {
    return this.HavePosition() ? `${this.result.position} место из ${this.result.teams}` : ""
}

Quiz.prototype.FormatPlayers = function() {
    return this.HavePlayers() ? GetWordForm(this.result.players, ["игрок", "игрока", "игроков"]) : ""
}

Quiz.prototype.GetUpdateParams = function() {
    let date = this.dateInput.GetValue()
    if (date === null)
        return null

    let time = this.timeInput.GetValue()
    if (time === null)
        return null

    let name = this.nameInput.GetValue()
    if (name === null)
        return null

    let shortName = this.shortNameInput.GetValue()
    if (shortName === null)
        return null

    let description = this.descriptionInput.GetValue()
    if (description === null)
        return null

    let cost = this.costInput.GetValue()
    if (cost === null)
        return null

    let placeId = +this.placeInput.value
    let organizerId = +this.organizerInput.value
    let category = this.categoryInput.value

    let position = this.positionInput.GetValue()
    if (position === null)
        return null

    let teams = this.teamsInput.GetValue()
    if (teams === null)
        return null

    position = position === "" ? 0 : +position
    teams = teams === "" ? 0 : +teams

    if (position > teams) {
        this.positionInput.Error("Место не может быть больше количества команд")
        return null
    }

    let players = this.playersInput.GetValue()
    if (players === null)
        return null

    players = players === "" ? 0 : +players

    return {
        quiz_id: this.quizId,
        name: name,
        short_name: shortName,
        description: description,
        datetime: ParseDatetime(new Date(date), time),
        cost: +cost,
        place_id: placeId,
        organizer_id: organizerId,
        category: category,
        album_id: this.albumId,
        result: position || teams || players ? {position: position, teams: teams, players: players} : null,
        participants: this.participantsInput.GetSelected()
    }
}

Quiz.prototype.Remove = function() {
    return SendRequest("/remove-quiz", {quiz_id: this.quizId}).then(response => {
        if (response.status != SUCCESS_STATUS) {
            ShowNotification(`Не удалось удалить квиз "${this.name}".<br><b>Причина</b>: ${response.message}`, "error-notification")
            return false
        }

        ShowNotification("Квиз успешно обновлён", "success-notification")
        return true
    })
}

Quiz.prototype.Update = function() {
    let params = this.GetUpdateParams()
    if (params === null)
        return

    console.log(params)
    SendRequest("/update-quiz", params).then(response => {
        if (response.status != SUCCESS_STATUS) {
            ShowNotification(`Не удалось обновить квиз "${this.name}".<br><b>Причина</b>: ${response.message}`, "error-notification")
            return
        }

        ShowNotification("Квиз успешно обновлён", "success-notification")
        this.UpdateParams(response.quiz)
    })
}

Quiz.prototype.UpdateParams = function(quiz) {
    this.quizId = quiz.quiz_id
    this.name = quiz.name
    this.shortName = quiz.short_name
    this.description = quiz.description
    this.datetime = new Date(quiz.datetime)
    this.cost = quiz.cost
    this.placeId = quiz.place_id
    this.organizerId = quiz.organizer_id
    this.category = new Category(quiz.category)

    this.albumId = quiz.album_id
    this.participants = quiz.participants
    this.result = quiz.result
}
